import EnhancedImage from '@components/common/EnhancedImage/EnhancedImage';
import { getCountdown } from '@utils/date-utils';
import { padZero } from '@utils/number-utils';
import { motion, Variants } from 'framer-motion';
import useTranslation from 'next-translate/useTranslation';
import { useState, useEffect } from 'react';

const EVENT_DATE = new Date('2022-02-01T04:00Z');
function calculateDistance() {
  return EVENT_DATE.getTime() - new Date().getTime();
}

function XmasHero() {
  const { t } = useTranslation();
  const [distance, setDistance] = useState<number>(calculateDistance);

  const { days, hours, minutes, seconds } = getCountdown(distance);

  useEffect(() => {
    const timerId = setInterval(() => setDistance(calculateDistance()), 1000);

    return () => clearInterval(timerId);
  }, []);

  return (
    <section tw="relative isolate col-span-full">
      <DesktopBackground tw="hidden xl:block" />
      <MobileBackground tw="xl:hidden" />
      <div tw="absolute-cover z-10 text-white flex flex-col text-center items-center justify-center font-sans md:(h-full)">
        <HeroLetters />
        {days >= 0 && (
          <div tw="mt-7 xl:mt-16">
            <div tw="flex gap-md justify-evenly w-full">
              <CountDownBlock
                tw="text-black"
                count={days}
                unit={t('days', { count: days })}
              />
              <CountDownBlock
                count={hours}
                unit={t('hours', {
                  count: hours,
                })}
              />
              <CountDownBlock
                count={minutes}
                unit={t('minutes', {
                  count: minutes,
                })}
              />
              <CountDownBlock
                count={seconds}
                unit={t('seconds', {
                  count: seconds,
                })}
              />
            </div>

            <div tw="mt-6 xl:mt-12">
              <p tw="color[rgba(255, 255, 255, 0.7)] font-thin mt-md">
                {t('Lunar New Year is coming!')}
                <br />
                {t('Celebrate Tết Nhâm Dần with events from VAS!')}
              </p>

              <button tw="mt-6 xl:mt-7 text-black text-sm font-bold background[#FFFFFF]  transition-all hover:(filter brightness-90) py-sm px-md  rounded-lg  md:(py-3 px-lg text-lg)">
                <a
                  href="https://bit.ly/TếtSeneca2022"
                  target="_blank"
                  rel="noreferrer noopener"
                >
                  {t('Learn more')}
                </a>
              </button>
            </div>
          </div>
        )}
      </div>
    </section>
  );
}

type CountDownBlockProps = {
  count: number;
  unit: string;
};
function CountDownBlock({ count, unit }: CountDownBlockProps) {
  return (
    <div tw="flex flex-col items-center background[rgba(255, 255, 255, 0.1)] flex-1 rounded-lg py-4 md:p-6">
      <span tw="text-2xl font-bold md:text-6xl lg:text-6xl">
        {padZero(count)}
      </span>
      <span tw="text-sm">{unit}</span>
    </div>
  );
}

type MobileBackgroundProps = { className?: string };
function MobileBackground(props: MobileBackgroundProps) {
  return (
    <EnhancedImage
      width={416}
      height={736}
      layout="responsive"
      tw="w-full"
      alt=""
      aria-hidden
      src={require('images/Background-mobile.jpg')}
      lqip={require('images/Background-mobile.jpg?lqip')}
      className={props.className}
    />
  );
}

